import styled from 'styled-components'
import {Link as LinkR} from 'react-router-dom'
import {Link as LinkS} from 'react-scroll'

export const Nav = styled.nav `
background: #000;
height:80px;

display: flex;
justify-content: center;
align-items: center;
z-index:10;
font-size: 1rem;
position: sticky;


@media screen and (max-width: 960px){
    transition: 0.8s all ease;

}

`

export const NavLogo = styled(LinkR)` 
color: red;
justify-self: flex-start;
cursor: pointer;
font-size: 1 rem;
display: flex;
align-items: center;
margin-left: 24px;
font-weight: bold;
text-decoration: none;
`
export const MobileIcon = styled.div ` 
display:none;

@media screen and (max-width:760px){
    display: block;
    position: absolute;
    top: 0;
    right: 0;
    transform: translate(-100%,60%);
    font-size: 1 rem;
    cursor: pointer;
    color: #fff;

}`

export const NavMenu = styled.ul `
display: flex;
align-items: center;
list-style: none;
text-align: center;
margin-right: -22px;

@media screen and (max-width:760px){
    display: none;
}
`
export const NavItem= styled.li ` 
height: 80px;
`
export const NavLinks= styled(LinkS) ` 
color: white;
display: flex;
align-items: center;
text-decoration: none;
padding: 0 1rem;
height: 100%;
cursor: pointer;

&.active{
    border-bottom: 3px solid #01b7f1;
}
`
